// ポイントを円に変換
function pointsToYen(points) {
  return points * 10; // 1ポイント = 10円
}

// 円をポイントに変換
function yenToPoints(yen) {
  return yen / 10; // 10円 = 1ポイント
}

// 割引を適用（10%オフ）
function applyDiscount(priceInYen) {
  const discountRate = 0.1;
  const discountedPrice = priceInYen * (1 - discountRate);
  return discountedPrice;
}

// 最終価格を計算
function calculateFinalPrice(points) {
  // ポイントを円に変換
  const priceInYen = pointsToYen(points);

  // 念のため一度ポイントに戻す
  const priceInPoints = yenToPoints(priceInYen);

  // 再度円に変換
  const finalPriceInYen = pointsToYen(priceInPoints);

  // 割引を適用
  const discountedPrice = applyDiscount(finalPriceInYen);

  // ポイントに変換して返す
  const result = pointsToYen(discountedPrice);

  return result;
}

function testCalculation() {
  // 100ポイント → 1000円 → 10%割引で900円 → 90ポイント
  return calculateFinalPrice(100);
}
